package com.loops;

public class NestedLoops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++) {
			for(int j=1;j<=2;j++) {
				for(int k=1;k<=1;k++) {
					System.out.println("i:"+i+" j:"+j+" k:"+k);
				}
			}
			
		}

	}

}
